import { Component, OnInit } from '@angular/core';
import { QualificationsService } from '../qualifications.service';

@Component({
  selector: 'app-qualifications',
  templateUrl: './qualifications.component.html',
  styleUrls: ['./qualifications.component.css']
})
export class QualificationsComponent implements OnInit{
  qualisummary:any=[];
  quali:any=[];
  showdet=false;
  university:string="";
  startdate:number=0;
  enddate:number=0;
  showdeta=false;
  cgpa:number=0;

  selectedIndex:number=-1;

constructor(public qualification:QualificationsService){
}
onhandle(index: number) {
  this.selectedIndex = index;
}
  handlek($event:any){
  if($event.target.innerHTML=="Graduation"){
    this.showdet=!this.showdet;
    this.university="GMU";
    this.startdate=2022;
    this.enddate=2023;
    this.cgpa=3.5;
  }
  else($event.target.innerHTML=="Undergraduation")
  {
    this.showdeta=!this.showdeta;
    this.university="SNIST";
    this.startdate=2022;
    this.enddate=2023;
    this.cgpa=3.5;
  }
}
ngOnInit(){
  this.qualisummary=this.qualification.getacademics();

  this.quali=[
    {
      type:"Education", Details:[ { first: 'Graduation', university: 'GMU', startdate: 2022, enddate: 2023, cgpa: 3.5 },
      { first: 'Undergraduation', university: 'SNIST', startdate: 2022, enddate: 2023, cgpa: 3.5 },
      { first: 'Schooling', university: 'School Name', startdate: 2010, enddate: 2020, cgpa: 'NA' }]
    },
    {
      type:"WorkExperience", Details:[{first:'Nuronics lab',second:'TCS',third:'GMU'}]
    },
  ];

}


}
