import { Component } from '@angular/core';

@Component({
  selector: 'app-academics',
  templateUrl: './academics.component.html',
  styleUrls: ['./academics.component.css']
})
export class AcademicsComponent {
  name:string="";
  pass:number=0;
  enter:number=0;
  cgpa:number=0;
  universitydetails=false;
  underuniversitydetails=false;
  Graduate(){
    this.universitydetails=true,
     this.name="George Mason University",
     this.enter=2022,
     this.pass=2023,
     this.cgpa=3.5
  }
  Undergraduate(){
    this.underuniversitydetails=true,
     this.name="Sreenidhi Institute of Science and Technology",
     this.enter=2017,
     this.pass=2021,
     this.cgpa=3.8
  }
}
