import { Component,OnInit} from '@angular/core';
import { AcademicService } from '../academic.service';
@Component({
  selector: 'app-academics',
  templateUrl: './academics.component.html',
  styleUrls: ['./academics.component.css']
})
export class AcademicsComponent {

EducationSummary:any=[];
edu:any=[];
one:string="";
two:string="";
search:string="";
showuniversity=false;
selectedEducationType: string = '';
constructor(public academicserice:AcademicService){

}
ngOnInit() {
  // Fetch the initial education data
  this.EducationSummary = this.academicserice.geteducation();
  this.edu = [
    {
      Type: "Grad", Details: [{ university: "GMU", startdate: 2022, passout: 2023, CGPA: 3.5, MAJORS: "Masters in CS" }]
    },
    {
      Type: "UnderGrad", Details: [{ university: "GMU", startdate: 2022, passout: 2023, CGPA: 3.5, MAJORS: "Masters in CS" }],
    }
  ]
}

grad() {
  this.showuniversity = !this.showuniversity;
  this.one = "GRAD";
  this.two = "Undergrad";
  this.selectedEducationType = 'Grad'; // Set the selected education type to "Grad"
}
handlefunction(){
  this.one="GRAD";
  this.two="Undergrad";
}
}

