import { Component } from '@angular/core';
import { Academic } from 'src/academic';
@Component({
  selector: 'app-academics',
  templateUrl: './academics.component.html',
  styleUrls: ['./academics.component.css']
})
export class AcademicsComponent {

 // academic:Academic[]=new Academic();
 universityDetailsArray: any[] = [
  { type: 'Graduate', name: 'GMU', enter: 2021, pass: 2025, cgpa: 3.8 },
  { type: 'Undergraduate', name: 'SNIST', enter: 2018, pass: 2022, cgpa: 3.5 },
  // Add more universities and their details here as needed
];

selectedUniversity: any = null;

handleClick(university: any) {

  this.selectedUniversity =university;
  console.log(this.selectedUniversity);
}
}
