import { Component, OnInit } from '@angular/core';
import { QualificationsService } from '../qualifications.service';

@Component({
  selector: 'app-qualifications',
  templateUrl: './qualifications.component.html',
  styleUrls: ['./qualifications.component.css']
})
export class QualificationsComponent implements OnInit{
  qualisummary:any=[];
  quali:any=[];
  show=false;
  showud=false;
  selectedIndex:number=-1;

constructor(public qualification:QualificationsService){
}
onhandle(index: number) {
  this.selectedIndex = index;
}
ngOnInit(): void {
  this.qualisummary=this.qualification.getacademics();

  this.quali=[
    {
      type:"Education", Details:[{first:'Graduation',second:'Undergraduation',third:'Schooling'}]
    },
    {
      type:"WorkExperience", Details:[{first:'Nuronics lab',second:'TCS',third:'GMU'}]
    },
  ];

}


}
<div *ngFor="let qualify of quali;let i=index">
<button (click)="onhandle(i)">
    {{qualify.type}}</button><br>

   <div *ngIf="i===selectedIndex">
    <div *ngFor="let det of qualify.Details" >
        <button>{{det.first}}</button><BR>
        <button>{{det.second}}</button><br>
        <button>{{det.third}}</button><br>
    </div>
</div>

</div>
